import { useState } from "react";

import MeetupList from "../components/meetups/MeetupList";
const DUMMY_DATA = [
    {
        id: 'm1',
        title: 'This is a first meetup',
        image:
            "https://images.unsplash.com/photo-1582653291997-079a1c04e5a1?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8Y29uZmVyZW5jZSUyMHJvb218ZW58MHx8MHx8&w=1000&q=80",
        address: 'Meetupstreet 5, 12345 Meetup City',
        description:
            'This is a first, amazing meetup which you definitely should not miss. It will be a lot of fun!',
    },
    {
        id: 'm2',
        title: 'This is a second meetup',
        image:
            "https://admission.tdtu.edu.vn/sites/admission/files/tochuc.jpg",
        address: 'Meetupstreet 5, 12345 Meetup City',
        description:
            'This is a second, amazing meetup which you definitely should not miss. It will be a lot of fun!',
    },
];

function AllMeetupsPage() {
    const [isLoading, setIsLoading] = useState(true);
    const [loadedMeetups, setLoadedMeetups] = useState([]);
    
    fetch(
        'https://react-meetup-6d00f-default-rtdb.firebaseio.com/meetups.json'
    ).then((response) => {
        return response.json();
    }).then(data => {
        setIsLoading(false);
        setLoadedMeetups(data);
    });

    if(isLoading) {
        return (
            <section>
                <p>Loading...</p>
            </section>
        )
    }


    return (
        <section>
            <h1>All Meetups</h1>
                <MeetupList meetups = {load}/>

        </section>
    );
}

export default AllMeetupsPage;
